import React, { Component} from 'react';
import MediaQuery from 'react-responsive'
import Fade from 'react-reveal/Fade';
import Carousel from 'react-elastic-carousel'
import img1 from '../Images/img1.svg';
import med from '../Images/med.svg';
import nac from '../Images/nacia.svg'
import plan from '../Images/PLANNING.svg'

const CarouselItems = [
    { id: 1, img: med, title: 'Здравоохранение', text: "381,6 млрд рублей" },
    { id: 2, img: nac, title: 'Национальная экономика', text: "1042,6 млрд рублей" },
    { id: 3, img: img1, title: 'Социальная политика', text: "551,8 млрд рублей" },
    { id: 4, img: img1, title: 'Образование', text: "479,7 млрд рублей" },
    { id: 5, img: img1, title: 'Физическая культура и спорт', text: "68,0 млрд рублей" }
];

const AnimatedListItems = [
    { id: 1, img: img1, title: 'лист Загаловок 1', text: "Лист текст 1" },
    { id: 2, img: img1, title: 'лист Загаловок 2 ', text: "Лист текст 2" },
];

const animateListDesktop = [
    { id: 1, Text:  
    <span>
    <div className='d-flex' id='carousel'>
        <Carousel className="styling-example p-5" itemsToShow={1}>
            {CarouselItems.map((item, key) => (
                <div className="Item" key={key}>
                    <p><img src={ item.img } alt=""></img></p>
                    <p><b>{ item.title }</b></p>
                    <p>{ item.text }</p>
                </div>
            ))}
        </Carousel> 
        <div className='p-5'>
            <h1 className='text-bold'>{ AnimatedListItems[0].title }</h1> 
            <p> { AnimatedListItems[0].text }</p>
        </div>
    </div>
    </span>, Bg: 'white'},
    { id: 2, Text: 
    <span className='block d-flex'>
        <div className="p-5">
            <img src={ AnimatedListItems[1].img } alt="" height="355px" width="355px"/>
        </div>
        <div className='p-5 desktop'>
            <h1 className='font-weight-bold display-1'>{ AnimatedListItems[1].title }</h1>
            <p/>{ AnimatedListItems[1].text }<p/>
        </div>
    </span>, Bg: 'blue'},
];

const animateListMobile = [
    { id: 1, Text:
    <span >
    <div className='paddingLR'>
        <div className='vam'>
            <h1 className='text-bold'>{ AnimatedListItems[0].title }</h1> 
            <p> { AnimatedListItems[0].text }</p>
        </div>
        <Carousel className="styling-example-mobile vam" itemsToShow={1}>
            {CarouselItems.map((item, key) => (
                <div className="ItemMobile" key={key}>
                    <p><img src={ item.img } alt=""></img></p>
                    <p><b>{ item.title }</b></p>
                    <p>{ item.text }</p>
                </div>
            ))}
        </Carousel> 
    </div>
    </span>, Bg: 'white'},
    { id: 2, Text: 
    <span className='paddingLR'>
        <div>
            <img src={ AnimatedListItems[1].img } alt="" height="200px" width="200px" />
        </div>
        <div className='mobile'>
            <h1 className='font-weight-bold display-1'>{ AnimatedListItems[1].title }</h1>
            <p/>{ AnimatedListItems[1].text }<p/>
        </div>
    </span>, Bg: 'blue'},
];

export default class Home extends Component {
    render() {
        return (
            <div className='pad border'>
            <Carousel verticalMode  className="styling-example" itemsToShow={1}>
            {CarouselItems.map((item, key) => (
                <div key={key}>
                    <p><img src={ item.img } alt=""></img></p>
                    <p><b>{ item.title }</b></p>
                    <p>{ item.text }</p>
                </div>
            ))}
            </Carousel> 
            </div>
            // <div className="Home">
            // {/* mobile */}
            // <MediaQuery minWidth={12} maxWidth={999} >
            //     <div className="padMob2 border" >
            //         <div className="text-center text-main animated fadeInUp ">
            //             <h1 className='display-4 text-bold p-4'><b>РОЛЬ БЮДЖЕТА ГОРОДА МОСКВЫ ДЛЯ ГРАЖДАН</b></h1>
            //             <h1 className='paddingLR'>
            //                 бюджет для граждан призван помогать 
            //                 информировать жителей Москвы 
            //                 о финансовой политике, 
            //                 решениях, которые принимает 
            //                 правительство для снижения 
            //                 уровня бедности
            //             </h1>
            //         </div>
            //     </div>
            //     {animateListMobile.map((item, key) => (
            //     <div style={styles.block} className={item.Bg} key={key}>
            //     <Fade bottom big >
            //         <div className='blockMobile mobile d-flex'>
            //             {item.Text}
            //         </div>
            //     </Fade>
            //     </div>
            //     ))}
            // </MediaQuery>

            // {/* desktop */}
            // <MediaQuery minWidth={1000}>
            //     <div className="pad border">
            //         <div className="text-center text-main animated fadeInUp">
            //             <h1 className='display-4 text-bold p-5'><b>РОЛЬ БЮДЖЕТА ГОРОДА <br/> МОСКВЫ ДЛЯ ГРАЖДАН</b></h1>
            //             <h1 className=''>бюджет для граждан призван помогать <br/>
            //                 информировать жителей Москвы <br/>
            //                 о финансовой политике, <br/>
            //                 решениях, которые принимает <br/>
            //                 правительство для снижения <br/>
            //                 уровня бедности
            //             </h1>
            //         </div>
            //     </div>
            //     {animateListDesktop.map((item, key) => (
            //     <div style={styles.block} className={item.Bg} key={key}>
            //         <Fade bottom big >
            //         <div className='block desktop'>
            //             {item.Text}
            //         </div>
            //         </Fade>
            //     </div>
            //     ))}
            // </MediaQuery>
            // </div>
        );
    }
}

const styles = {
    block: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '1000px',
        borderBottom: '1px solid rgba(255,255,255,.2)',
        minWidth: '100%',
        minHeight: '100%'
    }
};
