import React, { Component } from 'react';
import MediaQuery from 'react-responsive'
import Fade from 'react-reveal/Fade';
import {  } from 'reactstrap';
import Carousel from 'react-elastic-carousel'
import '../App.css';
import img1 from '../Images/img1.svg';
import med from '../Images/med.svg';
import nac from '../Images/nacia.svg'
import plan from '../Images/PLANNING.svg'

const animateList = [
    { id: 1, Title: 'Для чего нужен бюджет для граждан?', NavVk: (
        function(){
            if (getCookie("islogin") === "true") {
                return ( 
                    <>
                    <NavLink tag={Link} to="/"><img className='avatar' height={50} width={50} src={ getCookie("image")} alt="" /></NavLink>
                    <button className='VkButton' onClick={ logoutHandler }>Выйти</button>
                    </>
                )
            } else{
                return <button className='VkButton' onClick={ loginHandler }>Войти с помощью Vk</button>
            }
        }
    ), Image: img1, Bg: 'black'},
];

export default class Home extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="Home">
                {animateList.map((item, key) => (
                <div style={styles.block} className={item.Bg} key={key}>
                    <Fade bottom big >
                        <div className='block'>
                            <div className="">
                                <img height="155px" width="155px" src={item.Image} alt="image"/>
                            </div>
                            <div className="mobile" ref={this.myRef}>
                                <h1 className='font-weight-bold'>{`${item.Title}`}</h1>
                                <p/>{`${item.Text}` }<p/>
                            </div>
                        </div>
                    </Fade>
                </div>
                ))}
            </div>
        );
    }
}

const styles = {
    block: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '1000px',
        borderBottom: '1px solid rgba(255,255,255,.2)',
        minWidth: '100%',
        minHeight: '100%'
    }
};
