import React, { Component, Fragment } from 'react';
import { useMediaQuery } from 'react-responsive';
import Fade from 'react-reveal/Fade';
import girl2 from '../Images/woman2.webp';
import './Test.css';


const animateList = [
    { id: 1, Title: '1', Text: '1', Image: girl2, Bg: 'black'},
    { id: 2, Title: 'Бюджет для граждан', Text: 'Бюджет для граждан – это документ (аналитический материал), разрабатываемый и публикуемый в открытом доступе финансовым органом соответствующего публично-правового образования в целях предоставления гражданам актуальной информации о бюджете и отчете о его исполнении в объективной, заслуживающей доверия, доступной и простой для понимания форме.', Image: girl2, Bg: 'blue'},
    { id: 3, Title: 'Бюджет', Text: 'Бюджет - форма образования и расходования денежных средств, предназначенных для финансового обеспечения задач и функций государства и местного самоуправления. В общих чертах, бюджет - это план доходов и расходов на очередной финансовый год и плановый период (два предстоящих года).', Image: girl2, Bg: 'black'},
    { id: 4, Title: '4', Text: '4', Image: girl2, Bg: 'blue'},
    { id: 5, Title: '5', Text: '5', Image: girl2, Bg: 'black'}
];

export default class ReactReveal extends Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
        this.isDesktopOrLaptop = this.isDesktopOrLaptop.bind(this);
        this.isTabletOrMobile = this.isBigisTabletOrMobileScreen.bind(this);
        this.isBigScreen = this.isBigScreen.bind(this);
    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }

    handleScroll(event) {
        if (350 < window.scrollY){
        }
    }
    
    isDesktopOrLaptop = useMediaQuery({
        query: '(min-width: 1224px)'
      })
    isBigScreen = useMediaQuery({ query: '(min-width: 1824px)' })
    isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })

    render() {
        return (
            <Fragment>
                <div className="pad" >
                    <div className="text-center text-white animated fadeInUp"><h1>
                             Бюджет – форма образования и расходования 
                        <br/>денежных средств, предназначенных для
                        <br/>финансового обеспечения задач и функций 
                        <br/>местного самоуправления
                    </h1></div>
                </div>
                {animateList.map((item, key) => (
                    <div style={styles.block} className={item.Bg} key={key}>
                        <Fade bottom big >
                            <div className='block d-flex'>
                                <div className="p-5">
                                    <img height="auto" width="auto" src={item.Image} alt="image"/>
                                </div>
                                    <h1>Device Test!</h1>
                                    {this.isDesktopOrLaptop && <p>You are a desktop or laptop</p>}
                                    {this.isBigScreen && <p>You  have a huge screen</p>}
                                    {this.isTabletOrMobile && <p>You are a tablet or mobile phone</p>}
                                    <p>Your are in {isPortrait ? 'portrait' : 'landscape'} orientation</p>
                                    {isRetina && <p>You are retina</p>}
                                <div ref={this.myRef}>
                                    <h1 className='Title'>{`${item.Title}`}</h1>
                                    <p className='Text' />{`${item.Text}` }<p/>
                                </div>
                            </div>
                        </Fade>
                    </div>
                ))}
            </Fragment>
        );
    }
}

const styles = {
    block: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '1000px',
        borderBottom: '1px solid rgba(255,255,255,.2)',
        minWidth: '100%',
        minHeight: '100%'
    }
};
