import React, { Component, Fragment } from 'react';
import Fade from 'react-reveal/Fade';
import girl2 from '../Images/woman2.webp';
import './Test.css';


const animateList = [
    { id: 1, Title: '1', Text: '1', Image: girl2, Bg: 'black text-dark'},
    { id: 2, Title: 'Бюджет для граждан', Text: 'Бюджет для граждан – это документ (аналитический материал), разрабатываемый и публикуемый в открытом доступе финансовым органом соответствующего публично-правового образования в целях предоставления гражданам актуальной информации о бюджете и отчете о его исполнении в объективной, заслуживающей доверия, доступной и простой для понимания форме.', Image: girl2, Bg: 'blue text-white'},
    { id: 3, Title: 'Бюджет', Text: 'Бюджет - форма образования и расходования денежных средств, предназначенных для финансового обеспечения задач и функций государства и местного самоуправления. В общих чертах, бюджет - это план доходов и расходов на очередной финансовый год и плановый период (два предстоящих года).', Image: girl2, Bg: 'black text-dark'},
    { id: 4, Title: '4', Text: '4', Image: girl2, Bg: 'blue text-white'},
    { id: 5, Title: '5', Text: '5', Image: girl2, Bg: 'black text-dark'}
];

export default class ReactReveal extends Component {
    constructor(props) {
        super(props);
        this.myRef = React.createRef();
    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }

    handleScroll(event) {
        if (350 < window.scrollY){
        }
    }


    render() {
        return (
            <Fragment>
                <div className="pad" >
                    <div className="text-center text-white animated fadeInUp"><h1>
                             Бюджет – форма образования и расходования 
                        <br/>денежных средств, предназначенных для
                        <br/>финансового обеспечения задач и функций 
                        <br/>местного самоуправления
                    </h1></div>
                </div>
                {animateList.map((item, key) => (
                    <div style={styles.block} className={item.Bg} key={key}>
                        <Fade bottom big >
                            <div className="p-5">
                                <img height="350px" width="350px" src={item.Image} alt="image"/>
                            </div>
                            <div className="p-5" ref={this.myRef}>
                                <h1 className='Title'>{`${item.Title}`}</h1>
                                <p className='Text' />{`${item.Text}` }<p/>
                            </div>
                        </Fade>
                    </div>
                ))}
            </Fragment>
        );
    }
}

const styles = {
    block: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '1000px',
        borderBottom: '1px solid rgba(255,255,255,.2)',
        color: 'white'
    }
};
