import React, { Component } from 'react';
import MediaQuery from 'react-responsive'
import Fade from 'react-reveal/Fade';
import {  } from 'reactstrap';
import Carousel from 'react-elastic-carousel'
import '../App.css';
import img1 from '../Images/img1.svg';
import med from '../Images/med.svg';
import nac from '../Images/nacia.svg'
import plan from '../Images/PLANNING.svg'

const animateList = [
    { id: 1, Title: 'Для чего нужен бюджет для граждан?', 
    Text:  
    <Carousel className="styling-example" itemsToShow={1}>
        <div className="Item">
            <img src={med} alt=""></img>
            <h1>Здравоохранение</h1>
            <p>381,6 млрд рублей</p>
        </div>
        <div className="Item">
            <img src={nac} alt=""></img>
            <h1>Национальная экономика</h1>
            <p>1042,6 млрд рублей</p>
        </div>
        <div className="Item">
            <img src={img1} alt=""></img>
            <h1>Социальная политика</h1>
            <p>551,8 млрд рублей</p>
        </div>
        <div className="Item">
            <img src={img1} alt=""></img>
            <h1>Образование gjkbnbs</h1>
            <p>479,7 млрд рублей</p>
        </div>
        <div className="Item">
            <img src={img1} alt=""></img>
            <h1>Физическая культура и спорт</h1>
            <p>68,0 млрд рублей</p>
        </div>
    </Carousel>, 
    Bg: 'black'},
];

export default class Home extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="Home">
                {animateList.map((item, key) => (
                <div style={styles.block} className={item.Bg} key={key}>
                    <Fade bottom big >
                        <div className='block'>
                            <h1>{`${item.Title}`}</h1>
                            {item.Text}
                        </div>
                    </Fade>
                </div>
                ))}
            </div>
        );
    }
}

const styles = {
    block: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '1000px',
        borderBottom: '1px solid rgba(255,255,255,.2)',
        minWidth: '100%',
        minHeight: '100%'
    }
};
